                let time = this.current.time;
                // sycro if not idle on walk run leg position
                if( !isIdle ){ 
                    let ratio = this.current.getClip().duration / this.old.getClip().duration;
                    time = this.old.time * ratio;
                }

                // reset current
                this.current.reset();
                //currentEff = 0

                this.current.time = time;


