        const cVelLocal = TransformNormalToRef(cVel, bodyTransform(chassisBody, new three.Matrix4()).invert(),new three.Vector3());
        this.currentVehicleSpeedKmHour = cVelLocal.z;

        let forwardWorld = new three.Vector3();
